package Experiments;

public class ZeroByTwo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=2;
		int b=0;
		int c=3;
		int d=-1;
		int e=-2;
		int f=1;
		System.out.println(a/2);//1
		System.out.println(b/2);//0/2=0
		System.out.println(c/2);//3/2=1
		System.out.println(d/2);//-1/2=0
		System.out.println(e/2);//-1
		System.out.println(f/2);//0
		System.out.println(a%2);//0
		System.out.println(b%2);//0
		System.out.println(c%2);//1
		System.out.println(d%2);//-1
		System.out.println(e%2);//0
	}

}
